		 $(function() {
			$( "#legenddialog" ).hide();
			$(document).click(function()
			{
				$("#legenddialog").hide();
			});
		});

		function userclick(name,evt,up)
		{
			$.getJSON("whocolor.php?name="+name,function(data)
			{
				$("#legenddialog").html(data);
				$("#legenddialog").css({
					top: up ? 
						$("#"+name).offset().top - $("#legenddialog").height() - 26:
						$("#"+name).offset().top + 20,
					left: $("#"+name).offset().left + 30,
				}).show();
			});
		}
	</script>
